    		for (var i = Math.floor(l / 2); i >= 0; i--) {
    			_heapify(arr, i)
    		}
    	}

    	// 调整堆
    	function _heapify(arr, i) {
    		var left = 2 * i + 1,
    			right = 2 * i + 2,
    			max = i
    		if (left < l && arr[left] > arr[max]) {
    			max = left
    		}
    		if (right < l && arr[right] > arr[max]) {
    			max = right
    		}

    		if (max != i) {
    			var temp = arr[i]
    			arr[i] = arr[max]
    			arr[max] = temp

    			_heapify(arr, max)
    		}
    	}

    	_buildMaxHeap(arr)

    	for (var i = arr.length - 1; i > 0; i--) {
    		var temp = arr[0]
    		arr[0] = arr[i]
    		arr[i] = temp
    		l--
    		_heapify(arr, 0)
    	}
    	return arr
    }

